    for level, filename, n, expected in tests:
        imagename = filename.split('.pbm')[0].replace('-', '_')
        template = f'''
        def test_{imagename}_{n}(self):
            path = os.path.join('src', 'data', '{filename}')
            n = {n}
            img = load_pbm(path)
            expected = {expected}
            result = erosion(img, n)
            self.assertListEqual(result, expected)
        '''
        print(template)